userDefinedLinkHintCss:
/*
Gruvbox theme for Vimium
based on Dracula theme for Vimium by Trollwut (trollwut@trollwut.org).
*/

/* Link hint boxes */
div > .vimiumHintMarker {
  background: #32302f;
  font-size: 12px;
  border: 3px solid #ebdbb2;
  border-radius: 3px;
  box-shadow: 0em 0.1em 0.6em 0.1em rgba(0, 0, 0, 0.4);
}

/* Link hint text */
div > .vimiumHintMarker span {
  color: #ebdbb2;
  font-size: inherit;
  text-shadow: none;
}

/* Link hint matching characters */
div > .vimiumHintMarker > .matchingCharacter {
  color: #3c3836;
}

/* HUD ("heads-up display") bar */
div.vimiumHUD {
  background: #3c3836;
  border: none;
}

div.vimiumHUD .vimiumHUDSearchArea {
  background: #3c3836;
}

div.vimiumHUD .hud-find {
  background: #3c3836;
  border: none;
  color: #f8f8f2;
}

div.vimiumHUD span#hud-find-input{
  color: #f8f8f2;  /* 'Foreground' color */
}

div.vimiumHUD span#hud-match-count {
  color: #fbf1c7;  /* 'Comment' color */
}

div.vimiumHUD .vimiumHUDSearchAreaInner {
  color: #fbf1c7;  /* 'Comment' color */
}

#vomnibar{
    background-color: #fbf1c7;
}

#vomnibar input {
  color: #ebdbb2;
  font: -moz-window;
  /*font-family: 'Helvetica Neue', Helvetica, Arial, sans-serif;*/
  font-size: 20px;
  height: 26px;
  margin-bottom: 0;
  padding: 2px;
  background-color: #3c3836;
  border-radius: 0px;
  border: none;
  box-shadow: #fbf1c7 0px 0px 0px;
  width: 100%;
  outline: none;
  box-sizing: border-box;
}

#vomnibar .vomnibarSearchArea {
  display: block;
  padding: 10px;
  background-color: #3c3836;
  border-radius: 0px 0px 0 0;
  border-bottom: 0px solid #fbf1c7;
}

#vomnibar ul {
  background-color: #32302f;
  border-radius: 0 0 0px 0px;
  list-style: none;
  padding-top: 0;
  padding-bottom: 0;
  margin-block-start: 2px;
  margin-block-end: 0px;
}

#vomnibar li {
  border-bottom: 1px solid #3c3836;
  line-height: 1.1em;
  padding: 7px 10px;
  font-size: 16px;
  color: #f8f8f2;
  position: relative;
  display: list-item;
  margin: auto;
}

#vomnibar li .vomnibarBottomHalf {
  font-size: 15px;
  margin-top: 3px;
  padding: 2px 0;
}

#vomnibar li .vomnibarSource {
  color: #fb4934;
  margin-right: 4px;
}
#vomnibar li .vomnibarRelevancy {
  position: absolute;
  right: 0;
  top: 0;
  padding: 5px;
  background-color: #3c3836;
  color: #f8f8f2;
  font-family: monospace;
  width: 100px;
  overflow: hidden;
}

#vomnibar li .vomnibarUrl {
  white-space: nowrap;
  color: #83a598;
}

#vomnibar li .vomnibarMatch {
  font-weight: bold;
  color: #ebdbb2;
}

#vomnibar li em, #vomnibar li .vomnibarTitle {
  color: #689d6a;
  margin-left: 4px;
  font-weight: normal;
}
#vomnibar li em { font-style: italic; }
#vomnibar li em .vomnibarMatch, #vomnibar li .vomnibarTitle .vomnibarMatch {
  color: #ebdbb2;
}

#vomnibar li.vomnibarSelected {
  background-color: #3c3836;
  font-weight: normal;
}

#vomnibarInput::selection {
  background-color: #665c54;
}

